<?php add_filter( 'pre_option_link_manager_enabled', '__return_true' ); /***后台增加链接选项 ***//**评论样式**/function mytheme_comment($comment, $args, $depth) {		$GLOBALS['comment'] = $comment;		extract($args, EXTR_SKIP);		if ( 'div' == $args['style'] ) {			$tag = 'div';			$add_below = 'comment';		} else {			$tag = 'li';			$add_below = 'div-comment';		}?>		<<?php echo $tag ?><?php comment_class(empty( $args['has_children'] ) ? '' : 'parent') ?>id="comment-<?php comment_ID() ?>">		<?php if ( 'div' != $args['style'] ) : ?>		<div id="div-comment-<?php comment_ID() ?>" class="comment-body">		<?php endif; ?>		<div class="comment-author vcard">		<?php if ($args['avatar_size'] != 0) echo get_avatar( $comment, $args['avatar_size'] ); ?>		</div><?php if ($comment->comment_approved == '0') : ?>		<em class="comment-awaiting-moderation"><?php _e('Your comment is awaiting moderation.') ?></em>		<br /><?php endif; ?>		<div class="comment-meta commentmetadata"><a href="<?php echo htmlspecialchars( get_comment_link( $comment->comment_ID ) ) ?>">			<?php				/* translators: 1: date, 2: time */				printf( __('%1$s at %2$s'), get_comment_date(),  get_comment_time()) ?></a><?php edit_comment_link(__('(Edit)'),'  ','' );			?>			<?php printf(__('<cite class="fn">%s</cite> <span class="says"></span>'), get_comment_author_link()) ?>		</div>		<?php comment_text() ?>		<div class="reply">		<?php comment_reply_link(array_merge( $args, array('add_below' => $add_below, 'depth' => $depth, 'max_depth' => $args['max_depth']))) ?>		</div>		<?php if ( 'div' != $args['style'] ) : ?>		</div>		<?php endif; ?><?php        }				?><?php /** 获取文章第一张图片 **/ function catch_that_image() {  global $post, $posts;  $first_img = '';  ob_start();  ob_end_clean();  $output = preg_match_all('/<img.+src=[\'"]([^\'"]+)[\'"].*>/i', $post->post_content, $matches);  $first_img = $matches [1] [0]; if(empty($first_img)){   $first_img = '<img src="'.get_bloginfo('template_url').'/images/top.jpg"/>';   } else{ $first_img = '<img src="'.$first_img.'"/>'; }  return $first_img;    } ?><?php add_filter( 'show_admin_bar', '__return_false' );/**隐藏admin 导航条**/ ?>